// Gestos Touch
function inicializarGestosTouch() {
    let startX, startY, endX, endY;
    
    document.addEventListener('touchstart', (e) => {
        startX = e.touches[0].clientX;
        startY = e.touches[0].clientY;
    });
    
    document.addEventListener('touchend', (e) => {
        endX = e.changedTouches[0].clientX;
        endY = e.changedTouches[0].clientY;
        
        const deltaX = endX - startX;
        const deltaY = endY - startY;
        
        // Swipe horizontal (mudanÃ§a de seÃ§Ã£o)
        if (Math.abs(deltaX) > Math.abs(deltaY) && Math.abs(deltaX) > 50) {
            if (deltaX > 0) {
                // Swipe direita - seÃ§Ã£o anterior
                navegarSecaoAnterior();
            } else {
                // Swipe esquerda - prÃ³xima seÃ§Ã£o
                navegarProximaSecao();
            }
        }
        
        // Swipe vertical (fechar modais)
        if (Math.abs(deltaY) > Math.abs(deltaX) && Math.abs(deltaY) > 100) {
            if (deltaY > 0) {
                // Swipe para baixo - fechar modal
                fecharModal();
            }
        }
    });
    
    // Pull to refresh
    let pullStartY = 0;
    let isPulling = false;
    
    document.addEventListener('touchstart', (e) => {
        if (window.scrollY === 0) {
            pullStartY = e.touches[0].clientY;
            isPulling = true;
        }
    });
    
    document.addEventListener('touchmove', (e) => {
        if (isPulling && window.scrollY === 0) {
            const pullDistance = e.touches[0].clientY - pullStartY;
            if (pullDistance > 100) {
                // Pull to refresh
                window.location.reload();
                isPulling = false;
            }
        }
    });
}

