function gerarArquivoICS(eventos) {
    
    // FunÃ§Ã£o para formatar data no formato ICS
    function formatarDataICS(data) {
        return data.toISOString().replace(/[-:]/g, '').split('.')[0] + 'Z';
    }
    
    // FunÃ§Ã£o para escapar texto para ICS
    function escaparTextoICS(texto) {
        return texto
            .replace(/\\/g, '\\\\')
            .replace(/;/g, '\\;')
            .replace(/,/g, '\\,')
            .replace(/\n/g, '\\n')
            .replace(/\r/g, '');
    }
    
    let icsContent = `BEGIN:VCALENDAR
VERSION:2.0
PRODID:-//Sistema Legal//Sistema Legal//PT
CALSCALE:GREGORIAN
METHOD:PUBLISH
X-WR-CALNAME:Sistema Legal - Prazos e Vencimentos
X-WR-CALDESC:CalendÃ¡rio do Sistema Legal com prazos e vencimentos
X-WR-TIMEZONE:Europe/Lisbon`;

    eventos.forEach((evento, index) => {
        const dataInicio = new Date(evento.data);
        const dataFim = new Date(dataInicio.getTime() + (24 * 60 * 60 * 1000)); // +1 dia
        
        // Gerar UID Ãºnico
        const uid = `sistema-legal-${Date.now()}-${index}@sistemalegal.com`;
        
        // Formatar datas
        const dtstart = formatarDataICS(dataInicio);
        const dtend = formatarDataICS(dataFim);
        const dtstamp = formatarDataICS(new Date());
        
        // Escapar textos
        const summary = escaparTextoICS(evento.titulo);
        const description = escaparTextoICS(`Cliente: ${evento.cliente}${evento.valor ? `\\nValor: â‚¬${evento.valor}` : ''}\\n\\nSistema Legal - ANA PAULA MEDINA`);
        const location = escaparTextoICS('Sistema Legal');
        
        icsContent += `
BEGIN:VEVENT
UID:${uid}
DTSTAMP:${dtstamp}
DTSTART:${dtstart}
DTEND:${dtend}
SUMMARY:${summary}
DESCRIPTION:${description}
LOCATION:${location}
STATUS:CONFIRMED
TRANSP:OPAQUE
SEQUENCE:0
END:VEVENT`;
    });

    icsContent += `
END:VCALENDAR`;

    // Criar e baixar arquivo
    const blob = new Blob([icsContent], { 
        type: 'text/calendar;charset=utf-8',
        endings: 'native'
    });
    
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `Sistema_Legal_Calendario_${new Date().toISOString().split('T')[0]}.ics`;
    link.style.display = 'none';
    
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
    
    mostrarNotificacao(`Arquivo de calendÃ¡rio gerado com sucesso! (${eventos.length} eventos)`, 'success');
}

