/** Guarda a fatura selecionada como documento associado ao cliente. */
async function guardarFaturaComoDocumentoCliente() {
    const dados = obterDadosTemplateDocumento();
    if (!dados || dados.modeloId !== 'fatura_recibo' || !dados.fatura) {
        mostrarNotificacao('Selecione uma fatura primeiro.', 'warning');
        return;
    }
    if (!exigirPermissaoAcao('criar', 'documento')) return;
    const fatura = dados.fatura;
    const cliente = dados.cliente || {};
    const clienteId = fatura.clienteId || cliente.id;
    if (!clienteId) {
        mostrarNotificacao('Fatura sem cliente associado.', 'warning');
        return;
    }
    const lista = obterDocumentosAtual();
    const jaExiste = lista.some(d => String(d.faturaId) === String(fatura.id) && String(d.clienteId) === String(clienteId));
    if (jaExiste) {
        mostrarNotificacao('Esta fatura jÃ¡ estÃ¡ guardada nos documentos do cliente.', 'info');
        return;
    }
    const numero = fatura.numero || fatura.id || 'FAT';
    const documento = {
        id: gerarIdImutavel(),
        clienteId,
        clienteNome: fatura.clienteNome || cliente.nome || 'Cliente',
        faturaId: fatura.id,
        processoTipo: 'fatura_recibo',
        tipoArquivo: 'text/html',
        nomeArquivo: `Fatura ${numero}.html`,
        descricao: `Fatura ${numero} - ${fatura.clienteNome || cliente.nome || ''} - â‚¬${(parseFloat(fatura.valorTotal || fatura.valor) || 0).toFixed(2)}`,
        dataCriacao: new Date().toISOString()
    };
    try {
        if (isCloudReady() && typeof criarDocumentoCloud === 'function') {
            await criarDocumentoCloud(documento);
        } else {
            lista.unshift(documento);
            salvarDocumentosLocal(lista);
        }
        aplicarFiltrosDocumentos && aplicarFiltrosDocumentos();
        mostrarNotificacao(`Fatura ${numero} guardada nos documentos de ${documento.clienteNome}.`, 'success');
    } catch (e) {
        console.warn('Erro ao guardar fatura:', e);
        mostrarNotificacao('Erro ao guardar. Tente novamente.', 'error');
    }
}

async function gerarTemplateDocumento(acao) {
    if (!exigirPermissaoAcao('criar', 'documento')) {
        return;
    }
    const dados = obterDadosTemplateDocumento();
    if (!dados) return;

    const modelos = obterModelosDocumentos();
    const modelo = modelos.find(m => m.id === dados.modeloId);
    if (!modelo) {
        mostrarNotificacao('Modelo nÃ£o encontrado.', 'error');
        return;
    }

    const conteudoAdaptado = document.getElementById('templateConteudoAdaptavel')?.value?.trim();
    const conteudo = conteudoAdaptado || gerarConteudoTemplateDocumento(modelo.id, dados);
    const nomeBase = normalizarNomeArquivo(`${modelo.arquivo}_${dados.cliente?.nome || 'cliente'}`);
    const nomeArquivo = `${nomeBase || modelo.arquivo}.pdf`;

    if (acao === 'baixar' || acao === 'imprimir') {
        if (dados.modeloId === 'fatura_recibo' && dados.fatura && typeof gerarHtmlFaturaBilling === 'function') {
            try {
                let qrBase64 = '';
                const tempDiv = document.createElement('div');
                tempDiv.style.cssText = 'position:fixed;left:-9999px;opacity:0;pointer-events:none;';
                try {
                    if (typeof QRCode !== 'undefined') {
                        document.body.appendChild(tempDiv);
                        const fatura = dados.fatura;
                        const numero = fatura.numero || fatura.id || 'FAT-';
                        const sol = dados.solicitadora || DADOS_SOLICITADORA || {};
                        const siteUrl = (sol.websiteUrl || DADOS_SOLICITADORA?.websiteUrl || 'https://www.anapaulamedinasolicitadora.pt/').replace(/\/$/, '');
                        const qrData = `${siteUrl}/?fatura=${encodeURIComponent(numero)}`;
                        new QRCode(tempDiv, { text: qrData, width: 128, height: 128 });
                        const canvas = tempDiv.querySelector('canvas');
                        if (canvas) qrBase64 = canvas.toDataURL('image/png').replace(/^data:image\/png;base64,/, '');
                    }
                } finally {
                    if (tempDiv.parentNode) tempDiv.parentNode.removeChild(tempDiv);
                }
                dados.qrBase64 = qrBase64;
                const html = gerarHtmlFaturaBilling(dados);
                const janela = typeof abrirHtmlNovaJanela === 'function' ? abrirHtmlNovaJanela(html, acao === 'imprimir') : null;
                if (janela) {
                    mostrarNotificacao('Fatura aberta noutra janela. Use Ctrl+P para imprimir/guardar PDF.', 'success');
                } else {
                    mostrarNotificacao('Bloqueador de janelas impediu a abertura. Permita janelas para este site e tente novamente.', 'warning');
                }
                return;
            } catch (e) {
                console.warn('Fatura HTML falhou, a usar formato texto:', e);
            }
        }
        try {
            const jsPDF = (window.jspdf && window.jspdf.jsPDF) || (window.jspdf && window.jspdf.default) || window.jsPDF;
            if (!jsPDF) {
                mostrarNotificacao('Biblioteca PDF nÃ£o carregada. FaÃ§a Ctrl+F5 para recarregar a pÃ¡gina.', 'error');
                return;
            }
            const doc = new jsPDF({ format: 'a4', unit: 'mm' });
            const margin = 20;
            let y = await adicionarLogoAoPdf(doc, margin);
            doc.setFont('times', 'normal');
            doc.setFontSize(12);
            const pageWidth = doc.internal.pageSize.getWidth();
            const maxWidth = pageWidth - margin * 2;
            const lineHeight = 7;
            const blocos = conteudo.split('\n');
            blocos.forEach(bloco => {
                const linhas = doc.splitTextToSize(bloco || ' ', maxWidth);
                linhas.forEach(linha => {
                    if (y > 277) { doc.addPage(); y = margin; }
                    doc.text(linha, margin, y);
                    y += lineHeight;
                });
            });
            if (acao === 'baixar') {
                doc.save(nomeArquivo);
                mostrarNotificacao('Documento PDF gerado com sucesso!', 'success');
                return;
            }
            if (acao === 'imprimir') {
                const blob = doc.output('blob');
                const url = URL.createObjectURL(blob);
                const janela = window.open(url, '_blank');
                if (janela) {
                    janela.addEventListener('load', () => setTimeout(() => { janela.print(); URL.revokeObjectURL(url); }, 500));
                } else {
                    mostrarNotificacao('Permita abrir a janela para imprimir.', 'warning');
                    doc.save(nomeArquivo);
                    URL.revokeObjectURL(url);
                }
                return;
            }
        } catch (e) {
            console.error('Erro ao gerar PDF:', e);
            mostrarNotificacao('Erro ao gerar PDF. Verifique a consola.', 'error');
            return;
        }
    }

    const dataUri = `data:text/plain;charset=utf-8,${encodeURIComponent(conteudo)}`;
    const tamanho = new Blob([conteudo]).size;
        const documento = {
            id: gerarIdImutavel(),
        clienteId: dados.clienteId,
        clienteNome: dados.cliente?.nome || '',
        processoTipo: dados.processoTipo,
        descricao: `Modelo: ${modelo.nome}`,
        tags: ['template', modelo.nome],
        nomeArquivo,
        tipoArquivo: 'application/pdf',
        tamanho,
        conteudo: dataUri,
        criadoPor: appStorage.getItem('usuarioLogado') || 'N/D',
        tipoUsuario: appStorage.getItem('tipoUsuario') || 'N/D',
        dataCriacao: new Date().toISOString()
    };
    const lista = obterDocumentosAtual();
    lista.unshift(documento);
    salvarDocumentosLocal(lista);
    registrarAuditoria('criar', 'documento', `Documento modelo criado: ${documento.nomeArquivo}`, null, documento);
    mostrarNotificacao('Modelo gerado e guardado com sucesso!', 'success');
    aplicarFiltrosDocumentos();
}

