function uploadDocumento() {
    if (!exigirPermissaoAcao('criar', 'documento')) {
        return;
    }
    const arquivoInput = document.getElementById('docArquivo');
    const clienteId = parseIdSafe(document.getElementById('docCliente')?.value || '');
    const processoTipo = document.getElementById('docProcessoTipo')?.value || 'outro';
    const entidade = document.getElementById('docEntidade')?.value || '';
    const descricao = document.getElementById('docDescricao')?.value?.trim() || '';
    const tagsRaw = document.getElementById('docTags')?.value || '';
    const tags = tagsRaw.split(',').map(t => t.trim()).filter(Boolean);
    
    if (!clienteId) {
        mostrarNotificacao('Selecione o cliente do documento.', 'warning');
        return;
    }
    if (!arquivoInput || !arquivoInput.files || arquivoInput.files.length === 0) {
        mostrarNotificacao('Selecione um ficheiro.', 'warning');
        return;
    }
    const arquivo = arquivoInput.files[0];
    if (arquivo.size > 5 * 1024 * 1024) {
        mostrarNotificacao('Ficheiro acima de 5 MB. Tente um ficheiro menor.', 'error');
        return;
    }
    
    const cliente = clientes.find(c => c.id === clienteId);
    const reader = new FileReader();
    reader.onload = () => {
        const documento = {
            id: gerarIdImutavel(),
            clienteId,
            clienteNome: cliente ? cliente.nome : '',
            processoTipo,
            entidade,
            descricao,
            tags,
            nomeArquivo: arquivo.name,
            tipoArquivo: arquivo.type,
            tamanho: arquivo.size,
            conteudo: reader.result,
            criadoPor: appStorage.getItem('usuarioLogado') || 'N/D',
            tipoUsuario: appStorage.getItem('tipoUsuario') || 'N/D',
            dataCriacao: new Date().toISOString()
        };
        const lista = obterDocumentosAtual();
        lista.unshift(documento);
        salvarDocumentosLocal(lista);
        registrarAuditoria('criar', 'documento', `Documento adicionado: ${documento.nomeArquivo}`, null, documento);
        mostrarNotificacao('Documento adicionado com sucesso!', 'success');
        arquivoInput.value = '';
        const descInput = document.getElementById('docDescricao');
        const tagsInput = document.getElementById('docTags');
        if (descInput) descInput.value = '';
        if (tagsInput) tagsInput.value = '';
        aplicarFiltrosDocumentos();
    };
    reader.readAsDataURL(arquivo);
}

/** Abre HTML numa nova janela (usa Blob URL para renderizar corretamente). */
function abrirHtmlNovaJanela(html, autoPrint) {
    const blob = new Blob([html], { type: 'text/html;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const janela = window.open(url, '_blank', 'noopener,noreferrer');
    if (!janela) {
        URL.revokeObjectURL(url);
        return null;
    }
    janela.addEventListener('load', () => {
        URL.revokeObjectURL(url);
        if (autoPrint) setTimeout(() => janela.print(), 400);
    });
    janela.focus();
    return janela;
}

/** Gera o HTML da fatura a partir do documento (compartilhado por baixar e imprimir). */
function obterHtmlFaturaParaDocumento(doc) {
    const faturas = typeof obterFaturas === 'function' ? obterFaturas() : [];
    const fatura = faturas.find(f => String(f.id) === String(doc.faturaId));
    if (!fatura) return null;
    const cliente = (typeof obterClientePorIdOuNome === 'function' ? obterClientePorIdOuNome(fatura.clienteId, fatura.clienteNome) : null) || clientes.find(c => String(c.id) === String(fatura.clienteId));
    const pagamentosFatura = (Array.isArray(pagamentos) ? pagamentos : []).filter(p => String(p.faturaId) === String(fatura.id));
    let qrBase64 = '';
    if (typeof QRCode !== 'undefined') {
        const tempDiv = document.createElement('div');
        document.body.appendChild(tempDiv);
        const numero = fatura.numero || fatura.id || 'FAT-';
        const sol = DADOS_SOLICITADORA || {};
        const siteUrl = (sol.websiteUrl || 'https://www.anapaulamedinasolicitadora.pt/').replace(/\/$/, '');
        const qrData = `${siteUrl}/?fatura=${encodeURIComponent(numero)}`;
        new QRCode(tempDiv, { text: qrData, width: 128, height: 128 });
        const canvas = tempDiv.querySelector('canvas');
        if (canvas) qrBase64 = canvas.toDataURL('image/png').replace(/^data:image\/png;base64,/, '');
        document.body.removeChild(tempDiv);
    }
    const dados = { fatura, cliente: cliente || {}, solicitadora: DADOS_SOLICITADORA || {}, pagamentos: pagamentosFatura, qrBase64 };
    return typeof gerarHtmlFaturaBilling === 'function' ? gerarHtmlFaturaBilling(dados) : '';
}

